import NavBar from './component/NavBar';
import { Container } from 'react-bootstrap'
import MoviesList from './component/MovieList';
import axios from 'axios'
import { useEffect, useState } from 'react';
import{BrowserRouter , Routes ,Route } from 'react-router-dom'
import MovieDetails from './component/MovieDetails';
function App() {
  const [Movies, setMovies] = useState([])
  const [pageCount,setPageCount]=useState(0)
  //fetch data from online array
  const getAllMovies = async () => {
    const response = await axios.get("https://api.themoviedb.org/3/movie/popular?api_key=5e969379884d3ae7690fe6cf0d170a32&language=ar")
    setMovies(response.data.results)
    //console.log(response.data.results)
    setPageCount(response.data.total_pages)
  }

  /////
   //get current page
   const getPage = async (page) => {
    const response = await axios.get(`https://api.themoviedb.org/3/movie/popular?api_key=5e969379884d3ae7690fe6cf0d170a32&language=ar&page=${page}`)
    setMovies(response.data.results)
    //console.log(response.data.results)
    console.log(response.data.total_pages)
    setPageCount(response.data.total_pages)

  }

  //////////////////////
  useEffect(() => { //use data direct when component start 
    getAllMovies();
    //console.log(Movies)
  }, [])

  const search = async (word) => {
    if(word===""){ getAllMovies();
    }
    else{
    const response = await axios.get(`https://api.themoviedb.org/3/search/multi?api_key=5e969379884d3ae7690fe6cf0d170a32&query=${word}&language=ar-US&page=1&include_adult=false`)
    setMovies(response.data.results)
    //console.log(response.data.results)
    setPageCount(response.data.total_pages) //عشان يرجع يحسب عدد البيجز بعد السيرتش
    }}

  return (
    <div className="">
      <NavBar search={search} />
      <Container>
      <BrowserRouter> 
      <Routes>
      <Route  path="/" element={<MoviesList movies={Movies} getPage={getPage}  pageCount={pageCount}/>} />
      { /*  <MoviesList Movies={Movies} getPage={getPage} pageCount={pageCount} />
        <MovieDetails/>   */}

      <Route path="/movie/:id" element={<MovieDetails/>}  /> { /* :id  للدلالة انه متغير */}

      </Routes>
        </BrowserRouter>
      </Container>


    </div>

  );
}

export default App;
