import React from 'react'
import { Pagination } from 'react-bootstrap'
import ReactPaginate from "react-paginate"
const PaginationComponent = () => {
  const handlePageClick = () => {

  }
  const pageCount = 500;
  return (
    /*   from react bootstrap <Pagination>
     <Pagination.First />
     <Pagination.Prev />
     <Pagination.Item>{1}</Pagination.Item>
     <Pagination.Ellipsis />
 
     <Pagination.Item>{10}</Pagination.Item>
     <Pagination.Item>{11}</Pagination.Item>
     <Pagination.Item active>{12}</Pagination.Item>
     <Pagination.Item>{13}</Pagination.Item>
     <Pagination.Item disabled>{14}</Pagination.Item>
 
     <Pagination.Ellipsis />
     <Pagination.Item>{20}</Pagination.Item>
     <Pagination.Next />
     <Pagination.Last />
   </Pagination> */
    //from react pagination lib
    <ReactPaginate
      breakLabel="..."
      nextLabel="التالي"
      onPageChange={handlePageClick}
      marginPagesDisplayed={2} // يبين رقم اول صفحتين
      pageRangeDisplayed={2} //يظهر اخر 5 صفحات
      pageCount={pageCount}
      previousLabel=" السابق"
      //renderOnZeroPageCount={null}
      //use ready class from bootstrap with names props from reactPaginate library 
      containerClassName={"pagination justify-content-center p-3"}
      pageClassName={"page-item"}
      pageLinkClassName={"page-link"}
      previousClassName={"page-item"}
      nextClassName={"page-item"}
      previousLinkClassName={"page-link"}
      nextLinkClassName={"page-link"}
      breakClassName={"page-item"}
      breakLinkClassName={"page-link"}
      activeClassName={"active"}
    />

   
  )
}

export default PaginationComponent
