import React ,{useEffect , useState} from "react";
import { Row, Card, Col } from "react-bootstrap";
import CardMovie from "./CardMovie";
import PaginationComponent from "./pagination";
import { useSelector ,useDispatch } from "react-redux";
import {getAllMovie} from '../redux/actions/movieAction'

const MoviesList = ({ getPage ,pageCount }) => {

  const [Movies, setMovies] = useState([])
  //const [pageCount,setPageCount]=useState(0)
  //1 Redux
  const dispach=useDispatch();


    //////////////////////2
    useEffect(() => { //use data direct when component start 
      //getAllMovies();
      dispach(getAllMovie())
    }, [])
    ///3
    const dataMovies=useSelector((state)=>state.Movies)
    /////4
     useEffect(()=>{
setMovies(dataMovies)

     },[dataMovies])

  return (
    <Row className="mt-3">
    {Movies.length >=1 ? (Movies.map((movitem)=>{ //item is film in DB
      return(                                 //return 20 movies from DB
      <CardMovie key={movitem.id} movitem={movitem} /> //item عشان يرجع لكل فلم اسمه وخواصه مثل item.title item.category
      )
    })


    ) :<h3>no movies </h3>

    } 
    {Movies.length >=1 ?(<PaginationComponent getPage={getPage} pageCount={pageCount} />):null}
    </Row>
  );
};

export default MoviesList;
