import React from "react";
import { Row, Card, Col } from "react-bootstrap";
import CardMovie from "./CardMovie";
import PaginationComponent from "./pagination";
const MoviesList = ({ Movies , getPage ,pageCount }) => {
  return (
    <Row className="mt-3">
    {Movies.length >=1 ? (Movies.map((item)=>{ //item is film in DB
      return(                                 //return 20 movies from DB
      <CardMovie key={item.id} item={item} /> //item عشان يرجع لكل فلم اسمه وخواصه مثل item.title item.category
      )
    })


    ) :<h3>no movies </h3>

    } 
    {Movies.length >=1 ?(<PaginationComponent getPage={getPage} pageCount={pageCount} />):null}
    </Row>
  );
};

export default MoviesList;
