import NavBar from './component/NavBar';
import { Container } from 'react-bootstrap'
import MoviesList from './component/MovieList';
import axios from 'axios'
import { useEffect, useState } from 'react';
function App() {
  const [movies, setMovies] = useState([])
  //fetch data from online array
  const getAllMovies = async () => {
    const response = await axios.get("https://api.themoviedb.org/3/movie/popular?api_key=5e969379884d3ae7690fe6cf0d170a32&language=ar-US&page=1")
    setMovies(response.data.results)
    //console.log(response.data.results)
  }
  useEffect(() => { //use data direct when component start 
    getAllMovies();
    console.log(movies)
  }, [])

  const search = async () => {
    const response = await axios.get("https://api.themoviedb.org/3/movie/popular?api_key=5e969379884d3ae7690fe6cf0d170a32&language=ar-US&page=1")
    setMovies(response.data.results)
    //console.log(response.data.results)
  }
  return (
    <div className="">
      <NavBar />
      <Container>
        <MoviesList movies={movies} />
      </Container>


    </div>

  );
}

export default App;
